import axiosClient from './axiosClient';

const headers = {
  'authorization': 'Bearer ' + localStorage.getItem('token')
}

const CheckoutAPI = {
	postEmail: (query) => {
		const url = `http://localhost:5000/email${query}`;
		return axiosClient.post(url, {headers});
	},
};

export default CheckoutAPI;
