import { DateRange } from "react-date-range";
import { format } from "date-fns";

const Booking = () => {

    return (
        <div>
            <div className="hotelDetails">
                <div className="hotelDetailsTexts">
                    <h1 className="hotelTitle">Stay in the heart of City</h1>
                    <p className="hotelDesc">
                    Get your trip off to a great start with a stay at this property, which offers free Wi-Fi in all rooms. Strategically situated in Hoan Kiem District, allowing you access and proximity to local attractions and sights. Don't leave before paying a visit to the famous Old Quarter. Rated with 4 stars, this high-quality property provides guests with access to massage, restaurant and hot tub on-site.
                    </p>
                </div>
                <div className="hotelDetailsPrice">
                    <h1>Perfect for a 3-night stay!</h1>
                    <h2>
                        <b>$450</b> (3 nights)
                    </h2>
                    <button>Reserve or Book Now!</button>
                </div>
            </div>
            <div>
                <div className="lsItem">
                    <label>Check-in Date</label>
                    <span>{`${format(
                        date[0].startDate,
                        "MM/dd/yyyy"
                    )} to ${format(date[0].endDate, "MM/dd/yyyy")}`}</span>
                    {openDate && (
                        <DateRange
                        onChange={(item) => setDate([item.selection])}
                        minDate={new Date()}
                        ranges={date}
                        />
                    )}
                </div>
            </div>
        </div>
    )
};

export default Booking;
