import { DateRange } from "react-date-range";
import { format } from "date-fns";
import { useLocation } from "react-router-dom";
import { useState, useEffect } from "react";
import Navbar from '../../components/navbar/Navbar';
import MailList from '../../components/mailList/MailList';
import Footer from '../../components/footer/Footer';
import './booking.css'


const Booking = () => {
    const location = useLocation();
    const [date, setDate] = useState(location.state.date);
    const [user, setUser] = useState(location.state.user);
    const [hotel, setHotel] = useState(location.state.hotel);
    const [options, setOptions] = useState(location.state.options);
    const [rooms, setRooms] = useState([]);
    const [checked, setChecked] = useState(false);
    const [roomNumber, setRoomNumber] = useState(0);

    console.log(rooms);
    console.log(checked, roomNumber);
    console.log(rooms.filter(room => room.roomNumbers.includes(roomNumber)));
    console.log(options);

    useEffect(() => {
            const roomId = {
                id: hotel.rooms,
                options: options
            }

            const post = () => {
                fetch('http://localhost:5000/room', {
                    method: 'POST',
                    body: JSON.stringify(roomId),
                    headers: {
                        'Content-Type': 'application/json'
                    }
                })
                    .then(res => res.json())
                    .then(data => {
                        setRooms(data);
                    })
                    .catch(err => console.log(err));
            }

            post();
    }, [])

    const handleChange = (e) => {
        let isChecked = e.target.checked;
        let roomNumber = +e.target.value;
        setChecked(isChecked);
        setRoomNumber(roomNumber);
    }

    return (
        <div>
            <Navbar user={user} />
            <div className="wrapper">
                <div className="hotel">
                    <div className="hotel__details">
                        <h1>{hotel.name}</h1>
                        <p>{hotel.desc}</p>
                    </div>
                    <div className="hotel_booking">
                        <div className="box">
                            <h1>${hotel.cheapestPrice} (1 night)</h1>
                            <button className="upper-button">Reserve or Book Now!</button>
                        </div>
                    </div>
                </div>
                <div className="date__info">
                    <div className="date__info-date">
                        <h1>Dates</h1>
                            <DateRange
                                onChange={item => setDate([item.selection])}
                                showSelectionPreview = {true}
                                ranges={date}
                                direction="horizontal"
                            />
                    </div>
                    <div className="date__info-info">
                        <h1>Reserve Info</h1>
                        <form action="">
                            <div className="date__info-info-input">
                                <label htmlFor="">Your Full Name:</label>
                                <input type="text" placeholder="Full Name"/>
                            </div>
                            <div className="date__info-info-input">
                                <label htmlFor="">Your Email:</label>
                                <input type="text" placeholder="Email"/>
                            </div>
                            <div className="date__info-info-input">
                                <label htmlFor="">Your Phone Number:</label>
                                <input type="text" placeholder="Phone Number"/>
                            </div>
                            <div className="date__info-info-input">
                                <label htmlFor="">Your Indentity Card Number:</label>
                                <input type="text" placeholder="Indentity Card Number"/>
                            </div>
                        </form>
                    </div>
                </div>
                <div className="room">
                    <h1>Select Rooms</h1>
                    <div className="room__type">
                        {rooms ? rooms.map(room => (
                            <div className="room__type_n" key={room._id}>
                                <div className="room__type-info">
                                    <h3>{room.title}</h3>
                                    <p>{room.desc}</p>
                                    <p>Max people : <span>{room.maxPeople}</span></p>
                                    <h3>${room.price}</h3>
                                </div>
                                <div className="room_type-number">
                                    <ul className="room_type-number-list">
                                        {room.roomNumbers.map(n => (
                                            <li key={n}>
                                                <label htmlFor={n}>{n}</label>
                                                <input type="checkbox" value={n} onChange={(e) => handleChange(e, room.price)} />
                                            </li>
                                        ))}
                                    </ul>
                                </div>
                            </div>
                        ))
                            :
                        (<div className="room__type_n">
                            <div className="room__type-info">
                                <h3>Budget Double Room</h3>
                                <p>Pay nothing until Septermber 04, 2000</p>
                                <p>Max people : <span>2</span></p>
                                <h3>$350</h3>
                            </div>
                            <div className="room_type-number">
                                <ul className="room_type-number-list">
                                    <li>
                                        <label htmlFor="">101</label>
                                        <input type="checkbox" />
                                    </li>
                                    <li>
                                        <label htmlFor="">201</label>
                                        <input type="checkbox" />
                                    </li>
                                    <li>
                                        <label htmlFor="">202</label>
                                        <input type="checkbox" />
                                    </li>
                                    <li>
                                        <label htmlFor="">101</label>
                                        <input type="checkbox" />
                                    </li>
                                </ul>
                            </div>
                        </div>)}
                    </div>
                </div>
                <div className="payment">
                    <h1>Total Bill: $700</h1>
                    <div className="payment__details">
                        <div className="payment__details-method">
